import { createRule ,defineLinter } from "@typespec/compiler";


export const requiredDocRule = createRule({
  name: "no-model-doc",
  severity: "warning",
});
export const casingRule = createRule({
  name: "casing",
  severity: "warning",
});
export const testingRule = createRule({
  name: "testing",
  severity: "warning",
});

export const $linter = defineLinter({  
  rules: [requiredDocRule,casingRule,testingRule],
  ruleSets: {
    recommended: {
      enable: {[`fake-linter/${testingRule.name}`]: true}
    }
  }
});
