{
    prop = new Dictionary<string, object>
    {
        ["model"] = new
        {
            name = "<name>",
        },
        ["literal"] = null,
        ["int"] = null,
        ["boolean"] = null
    },
});
Response response = await client.SendAsync(content);

Console.WriteLine(response.Status);

